// <![CDATA[
function commas(num){  
  num=output=num.toString(),l=num.length;
  for(var i=l;i>-1;i=i-3){
    if(i!=0 && i!=l){
      output=output.substring(0,i)+','+output.substring(i)
    }
  }
  return output;
}

function cdtd() {
    var xmas = new Date("December 25, 2012 00:01:00");
    var now = new Date();
    var timeDiff = xmas.getTime() - now.getTime();
    if (timeDiff <= 0) {
        clearTimeout(timer);
        document.write("Christmas is here!");
        // Run any code needed for countdown completion here
    }
    var seconds = Math.floor(timeDiff / 1000);
        var totalseconds = Math.abs(seconds);
    var minutes = Math.floor(seconds / 60);
        var totalminutes = Math.abs(minutes);
    var hours = Math.floor(minutes / 60);
        var totalhours = Math.abs(hours);
    var days = Math.floor(hours / 24);
    var heartbeats = Math.floor(seconds / 60 * 65);
    var totalheart = Math.abs(heartbeats);
    hours %= 24;
    minutes %= 60;
    seconds %= 60;

    document.getElementById("daysBox").innerHTML = days;
    document.getElementById("secsBox").innerHTML = commas(totalseconds);
    document.getElementById("hoursBox").innerHTML = commas(totalhours);
    document.getElementById("minsBox").innerHTML = commas(totalminutes);
    document.getElementById("heartBox").innerHTML = commas(totalheart);
    var timer = setTimeout('cdtd()',1000);

    var oldMins = document.getElementById('minsBox').innerHTML;
    var newMins = oldMins + "<span style='color:#ff0000'>" + " Minutes Til' Christmas" + "</span>";
    document.getElementById('minsBox').innerHTML = newMins;
    var oldHours = document.getElementById('hoursBox').innerHTML;
    var newHours = oldHours + "<span style='color:#ff0000'>" + " Hours Til' Christmas" + "</span>";
    document.getElementById('hoursBox').innerHTML = newHours;
    var oldDays = document.getElementById('daysBox').innerHTML;
    var newDays = oldDays + "<span style='color:#ff0000'>" + " Days Til' Christmas" + "</span>";
    document.getElementById('daysBox').innerHTML = newDays;
    var oldSecs = document.getElementById('secsBox').innerHTML;
    var newSecs = oldSecs + "<span style='color:#ff0000'>" + " Seconds Til' Christmas" + "</span>";
    document.getElementById('secsBox').innerHTML = newSecs;
    var oldHeart = document.getElementById('heartBox').innerHTML;
    var newHeart = oldHeart + "<span style='color:#ff0000'>" + " Heartbeats Til' Christmas" + "</span>";
    document.getElementById('heartBox').innerHTML = newHeart;

}
