function checkevents(day, month, year, week, dayofweek) {

    var numevents = 0;
    var floater = 0;

    var len = events.length;

    len = document.getElementById("hidlength").value;

    for (var i = 0; i < len; i++) {

        if (events[i][1] == "Y" && events[i][0] == "3") {

            if ((events[i][3] == day) && (events[i][2] == month))
                numevents++;
        }

        else if (events[i][1] != "Y" && (events[i][0] == "1" || events[i][0] == "2")) {
            if ((events[i][3] == day) && (events[i][2] == month) && (events[i][4] == year))
                numevents++;
        }
    }

    if (numevents == 0) {
        return false;
    } else {
        return true;
    }
}

function bindimages(day, month, year, week, dayofweek) {


    var images = "";
    var bbacount = 0;
    var globalcount = 0;
    var bdaycount = 0;

    for (var i = 0; i < document.getElementById("hidlength").value; i++) {
        // First we'll process recurring events (if any):
        if (events[i][0] != "") {

            if (events[i][1] == "Y") {

                if (events[i][0] == "3") {

                    if ((events[i][3] == day) && (events[i][2] == month)) {
                        bdaycount = bdaycount + 1;

                        if (bdaycount == 1) {
                            images += '<img src="imagesphone/birthdayevents_iconsmall.png" height="11" width="11" />';
                        }
                    }
                }
            }

            else {

                if (events[i][0] == "1") {

                    if ((events[i][3] == day) && (events[i][2] == month) && (events[i][4] == year)) {
                        bbacount++;
                        if (bbacount == 1)
                            images += '<img src="imagesphone/BBAevents_iconsmall.png" height="11" width="11" />';
                    }
                }

                if (events[i][0] == "2") {

                    if ((events[i][3] == day) && (events[i][2] == month) && (events[i][4] == year)) {
                        globalcount++;
                        if (globalcount == 1)
                            images += '<img src="imagesphone/globalevents_iconsmall.png" height="11" width="11" />';

                    }
                }
            }
        }
    }

    globalcount = 0;
    bbacount = 0;
    bdaycount = 0;



    if (images == "") {
        return "";
    }

    else {

        return images;
    }
}

