function clearDefault(ele) {
    if (ele.defaultValue == ele.value) {
        ele.value = ''
    }
}

// To restore default value onblur="restore(this)"
function restore(ele) {
    if (ele.value == '') {
        ele.value = ele.defaultValue;
    }
}

function Validation(){
    if (document.getElementById('finenceUserName').value == "" || document.getElementById('finenceUserName').value=="Username") {
        //alert("Please Enter User Name");
         document.getElementById('alert').style.display = "block";
        document.getElementById('alertPtag').innerHTML = 'Please Enter User Name';
        document.getElementById('finenceUserName').focus();
        return false;
    }
     if (document.getElementById('finencePwd').value == "" || document.getElementById('finencePwd').value=="Password") {
        //alert("Please Enter Password");
         document.getElementById('alert').style.display = "block";
        document.getElementById('alertPtag').innerHTML = 'Please Enter Password';
        document.getElementById('finencePwd').focus();
        return false;
    }
    if (document.getElementById('finenceUserName').value != "" && document.getElementById('finencePwd').value != "") {
        //alert("username and password mismatch");
         document.getElementById('alert').style.display = "block";
        document.getElementById('alertPtag').innerHTML = 'username and password mismatch';
        document.getElementById('finenceUserName').value="Username";
        document.getElementById('finencePwd').value="Password";
        return false;
    }
}

