(function(){

/// hack - its callback or localhost
window.isGap = location.hash=='';

var BookApp = function(){
	
	var authorName = 'PudhumaiPithan',
	i, j,
	$w = $(window),
	ww, wh,
	
	$sections = $('#sections'),
	$section = $('#sections > section'),
	
	noOfSections = $section.length,
	
	/// iscrolls
	homeScroll, storyScroll,
	
	/// aliases
	$XXsections,
	
	$ch = $('<code id="ch"/>').appendTo('body'),
	
	isAnalytic = false,
	fn = function(){},
	localSetKey = function(key, val) {
		localStorage.setItem(key, val); 
	},
	localGetKey = function(key) {
		var val = localStorage.getItem(key);
		return val; //alert( "get | "+ key +':'+ val);
	},
	
	/// where - current section index
	_whr = 0,
	
	/// generate home ol list
	_generateHomeList = function() {
		//console.log('_generateHomeList | len: '+ books.homeList.length);
		
		var htm = '<li>'
		+ $.map(books.homeList, function(node_1) { //, i
			/// *** level 1 one
			
			return '<span>'+ node_1.name +'</span><ol><li>'
			+ $.map(node_1.children, function(node_2) {
				/// *** level 2 second
				
				return '<a>'+ node_2.name +'</a><ol><li>'
				+ $.map(node_2.children, function(node_3) {
					
					/// *** level 3 three - work titles
					//node_3.id +'| '+
					
					return '<a data-id="'+ node_3.id +'">'+ node_3.name +'</a>';
					
				}).join('</li><li>')
				+ '</li></ol>';
				
			}).join('</li><li>')
			+ '</li></ol>';
			
		}).join('</li><li>')
		+ '</li>';
		
		//console.log('_generateHomeList | htm: '+ htm);
		
		$('#homeList').html(htm);
		
		books.homeList = [];
		
	},
	
	
	_resize = function() {
		
		//ww = (isGap ? screen.width : $w.width()) + 1.5;
		ww = $w.width() + 1.5;
		wh = isGap ? screen.height : $w.height();
		
		//console.log('_resize | ('+ ww +','+ wh +') | ' + (ww * noOfSections)); // return;
		
		$sections.width(ww * noOfSections);
		_gotoSection(_whr);
	},
	
	/**
	 * usage
	 * (2) - goto section 2
	 * (0, 1) - goto next section
	 * (0, -1) - goto previous section
	 */ 
	_gotoSection = function(whr, toNext) {
		
		if( !(whr || toNext) ) return; /// invalid call
		
		var temp_whr = _whr;
		
		if( whr ) {
			temp_whr = whr;
		} else if( toNext ) {
			temp_whr += toNext;
		}
		
		//TODO no skipping of sections
		var left = whr
			? - ww * (whr-1) 
			: ((toNext==1 ?'-':'+') +'='+ ww);
		
		//console.log('_gotoSection: _whr:'+ _whr +'; toNext: '+ toNext);
		//console.log('_gotoSection: _whr:'+ _whr +'; sect: '+ sect.length);
		
		if( _whr != temp_whr )
		$section.css('visibility', 'visible');
		
		/// start moving
		$sections.css('left', left);
		
		_whr = temp_whr;
		
		/// hide others
		setTimeout(function(){
			///visibility: hidden;
			//$('#sections section:not(:nth-child('+ _whr +'))').css('visibility', 'hidden');
			
			$section.filter(function(index){
				//console.log('_gotoSection: _whr:'+ _whr +' | '+ index +' : '+ (index != (_whr - 1)));
				return index != (_whr - 1);
			}).css('visibility', 'hidden');
			
		}, 500);
		
	},
	
	_backToHome = function() {
		
		setTimeout(function(){
			$('#storyContent').html('');
		}, 250);
		
		_gotoSection(2);
	},
	
	_addButtonsListeners = function() {
		/// call this after generating the home list
		
		/* /d layout navigation
		$('div.nav a').click(function(){
			toNext = ($(this).html()=='&gt;') ? 1 : -1;
			_gotoSection(0, toNext);
		});*/
		
		/// level 2 - expand / collapse - sub topics
		$('#homeList > li > ol > li > a').click(function(){
			var tht = $(this),
				a = tht.next().find('a');
			
			if( a.length > 1 ) {
				tht.toggleClass('expand');
				tht.next().toggle('fast');
			} else {
				a.click();
			}
		});
		
		/// level 3 - book link
		$('#homeList > li > ol > li > ol > li > a').click(function(){
			
			var tht = $(this), thtID = tht.data('id'),
				workTitle = tht.html();
			
			//console.log('homeList::click | this: '+ thtID +' | '+ tht.html() +' | '+ books.works['w_'+thtID]);
			//alert(tht.html())
			//$('#storyTitle').html( tht.html() );
			
			/// note
			var ol = tht.parent().parent(),
				aLen = ol.find('a').length,
				topic = ol.parent().find('a:first').html();
			workTitle = topic + (aLen==1 ? '' : '<span class="nw"> : '+workTitle+'</span>');
			//alert( workTitle );
			
			$('a.last_read').removeClass('last_read');
			tht.addClass('last_read');
			
			//workTitle = thtID +': '+ workTitle; return;
			
			/// reset scroll pos
			storyScroll.scrollTo(0, 0, 0);
			
			$('#storyContent').html(
				  '<h2>'+ workTitle +'</h2>'
				+ books.works['w_'+thtID]
				+ '<div class="hr"/>'
			);
			
			_gotoSection(3);
			
			localSetKey('last_read', thtID);
			
			/// track work:thtID
			if( isAnalytic )
			window.plugins.analytics.trackEvent(authorName, 'work', ''+ thtID, 1, fn, fn);
			
		});

		$('#backToHome').click(_backToHome);
		
		$('#zoomBtn').click(function(){
			
			var tht = $('#zoomBtn'),
			zoom = parseInt( tht.data('zoom') );
			
			zoom++;
			(zoom > 3) && (zoom = 1);
			
			//alert(zoom);
			
			$('#story').removeClass('zoom_1 zoom_2 zoom_3').addClass('zoom_'+ zoom);
			
			tht.data('zoom', zoom);
			
		});
		
		/// btn touch effect
		$('a').click(function(){
			var tht = $(this);
			tht.addClass('btnDown');
			setTimeout(function(){
				tht.removeClass('btnDown');
			}, 120);
		});
		
		/// track More
		$('#moreapps').click(function(){
			if( isAnalytic ) window.plugins.analytics.trackEvent(authorName, 'e','More', 1,fn,fn);
		});
		
		/// note: about - click first
		$('#homeFooter').click(function(){
			$('#homeList > li:first > ol:first > li > ol > li > a').click();
			if( isAnalytic ) window.plugins.analytics.trackEvent(authorName, 'e','About', 1,fn,fn);/// track
		});
		$('#homeList li:first').hide();
	},
	
	
	_restoreLastRead = function() {
		var read = localGetKey('last_read');
		if( !read ) return;
		
		var a = $('#homeList > li > ol > li > ol > li > a[data-id='+ read +']');
		if( !a.length ) return; /// not found
		
		//alert( a.data('id') );
		
		/*note*/
		var ol = a.parent().parent();
		if( ol.find('a').length==1 ) return; /// just 1
		
		a.addClass('last_read');
		ol.parent().find('a:first').click();
		
	},
	
	
	_initApp = function() {
		
		//$sections $section noOfSections;
		//alert('_initApp | noOfSections:'+ noOfSections); return;
		
		$('#splashFooter').html( locale.splashFooter );
		$('#homeTitle, #storyTitle').html( books.author.title );
		$('#homeFooter').text( locale.homeFooter );
		
		/// when ready show
		$('#sections').show();
		
		_resize();
		$w.resize(_resize);
		
		_generateHomeList();
		
		_addButtonsListeners();
		
		/// init iScroll;
		homeScroll = new iScroll('homeScrollerWrapper', { hScroll:false, checkDOMChanges:true, scrollbarClass:'myScrollbar' });
		storyScroll = new iScroll('storyScrollerWrapper', { hScroll:true, checkDOMChanges:true, scrollbarClass:'myScrollbar' });
		
		setTimeout(_restoreLastRead, 3600);
		
	},
	
	_onBackKeyDown = function() {
		//alert('_onBackKeyDown | _whr:'+ _whr);return;
		
		if( _whr==3 )
			_backToHome();
		else
			navigator.app.exitApp();
	},
	
	_init = function() {
		
		//alert('bookapp:_init | isGap:'+ isGap);return;
		
		_initApp();
		
		_whr = 1;
		
		//return;
		
		/// skip splash screen after a delay
		setTimeout(function(){
			
			_gotoSection(0, 1);
			//d _gotoSection(4);
			
		}, isGap ? 3000 : 1000);
		
		//console.log('_init: '+ isGap);
		if( isGap ) {
			document.addEventListener('backbutton', _onBackKeyDown, false);
			
			/// track
			window.plugins.analytics.start('UA-29600184-1', function(){
				//alert('success1');
				
				isAnalytic = true;
				
				///Page: /PudhumaiPithan success
				window.plugins.analytics.trackPageView('/'+authorName, fn, fn);
				
				///Home
				window.plugins.analytics.trackEvent(authorName, 'e', 'Home', 2, fn, fn);
				
			}, function(){}//fail
			);
			
		}
	};
	
	_init();
	
};


/// body onload
$(function() {
	//alert('phonegap:'+ isGap);return;
	
	if(isGap)
		document.addEventListener('deviceready', onDeviceReady, true);
	else
		onDeviceReady();
});

function onDeviceReady() {
	
	window.bookApp = new BookApp();
	
	//console.log(isGap);alert( isGap +'\n'+ window.plugins.analytics );
}

/// stop touch move
document.addEventListener('touchmove', function(e){ e.preventDefault(); }, false);

})();





