REST_SERVER_URL = "http://www.popemilan.com/app/appscript/index.php";                                                                                                                  
TIMEOUT_AJAX_REQUESTS = 20000;                                                                                                        
																																			
NOME_DB = "Musei di Milano";                                                                                                                                  
VERSIONE_DB = "2.0";                                                                                                                          
DESCRIZIONE_DB = "Musei di Milano";                                                                                                                    
DIMENSIONE_DB = 2 * 1024 * 1024;
var db;                                                                                                                   
																																			
                                                                                                                                                        
function apriDb()                                                                                                                                       
{                                                                                                                                                       
	db = window.openDatabase(NOME_DB, VERSIONE_DB, DESCRIZIONE_DB, DIMENSIONE_DB);                                                                      
	return(db);                                                                                                                                         
}                                                                                                                                                       
                                                                                                                                                        
function eseguiSql(sql, parametri, successo, fallimento)                                                                                                
{                                                                                                                                                       
	if(!db) db = apriDb();                                                                                                                              
	db.transaction(function(tx) {                                                                                                                       
						tx.executeSql(sql, parametri, successo, fallimento);                                                                                            
					},
					function(tx,error){
						erroreSql(error, fallimento);
					}
	);
}                                                                                                                                                       
                                                                                                                                                        
function erroreSql(error, fallimento)                                                                                                                   
{                                                                                                                                                       
	//console.log(error);                                                                                                                                 
	fallimento(null, error);                                                                                                                            
}                                                                                                                                                       
                                                                                                                                                        
                                                                                                                                                        
/*                                                                                                                                                      
Inserisci righe in una tabella del database                                                                                                             
*/                                                                                                                                                      
                                                                                                                                                        
function inserisci(nomeTabella, colonne, oggetto, callback)                                                                                             
{
	inserimento = "INSERT INTO " + nomeTabella;                                                                                                         
	nomi = " (" + colonne.toString() + ")";                                                                                                             
	parametri = new Array();                                                                                                                            
	valori = " VALUES (";                                                                                                                               
	for (i = 0; i < colonne.length; i++) {                                                                                                              
		colonna = colonne[i];                                                                                                                           
		valori += "?,";                                                                                                                                 
		parametri.push(oggetto[colonna]);                                                                                                               
	}                                                                                                                                                   
	valori = valori.slice(0, valori.length - 1);                                                                                                        
	valori += ")";                                                                                                                                      
	sql = inserimento + nomi + valori;                                                                                                                  
	logga(sql);                                                                                                                                         
	logga(parametri);                                                                                                                                   
	eseguiSql(sql, parametri, function(){inserimentoCorretto(callback)}, function(){inserimentoErrato(sql, callback)});                                 
}                                                                                                                                                       
                                                                                                                                                        
function inserimentoCorretto(callback){
	//console.log("Insert success");
	callback(true);
}

function inserimentoErrato(sql, callback){
	//console.log("Impossibile effettuare operazione: " + sql);
	callback(false);
}
                                                                                                                                                        
/*                                                                                                                                                      
Aggiorna righe in una tabella del database                                                                                                              
*/                                                                                                                                                      
                                                                                                                                                        
function aggiorna(nomeTabella, colonne, oggetto, callback)                                                                                              
{                                                                                                                                                       
	inserimento = "UPDATE " + nomeTabella;                                                                                                              
	parametri = new Array();                                                                                                                            
	valori = " SET";                                                                                                                                    
	for (i = 0; i < colonne.length; i++) {                                                                                                              
		colonna = colonne[i];                                                                                                                           
		valori += " " + colonna + " = ?,";                                                                                                              
		parametri.push(oggetto[colonna]);                                                                                                               
	}                                                                                                                                                   
	valori = valori.slice(0, valori.length - 1);                                                                                                        
	valori += " WHERE id IS \"" + oggetto.id + "\"";                                                                                                    
	sql = inserimento + valori;                                                                                                                         
	logga(sql);                                                                                                                                         
	logga(parametri);                                                                                                                                   
	eseguiSql(sql, parametri, function(){aggiornamentoCorretto(callback)}, function(){aggiornamentoErrato(sql, callback)});                             
}                                                                                                                                                       
                                                                                                                                                        
function aggiornamentoCorretto(callback){
	//console.log("update succesfull");
	callback(true);
}

function aggiornamentoErrato(sql, callback){
	//console.log("Impossibile effettuare operazione: " + sql);
	callback(false);
}
                                                                                                                                                        
function _inserisci(nomeTabella, colonne, oggetto, callback)                                                                                            
{                                                                                                                                                       
	sql = "SELECT * FROM " + nomeTabella + " WHERE id IS ?";                                                                                            
	parametri = [oggetto.id];                                                                                                                           
	seleziona(sql, parametri, function(risultato, operazione){                                                                                          
		if(risultato) aggiorna(nomeTabella, colonne, oggetto, callback);                                                                                
		else inserisci(nomeTabella, colonne, oggetto, callback);                                                                                        
	});                                                                                                                                                 
}                                                                                                                                                       
                                                                                                                                                        
function aggiornaProprieta(nomeTabella, chiave, valoreChiave, proprieta, valoreProprieta, callback)                                                     
{                                                                                                                                                       
	sql = "UPDATE " + nomeTabella + " SET " + proprieta + " = " + valoreProprieta + " WHERE " + chiave + " IS ?";                                       
	parametri = [valoreChiave];                                                                                                                         
	eseguiSql(sql, parametri, function(){aggiornamentoCorretto(callback)}, function(){aggiornamentoErrato(sql, callback)});                             
}                                                                                                                                                       
                                                                                                                                                        
/*                                                                                                                                                      
Svuota tabella del database                                                                                                                             
*/                                                                                                                                                      
                                                                                                                                                        
function eliminaRighe(nomeTabella, chiave, valoreChiave, callback)                                                                                      
{                                                                                                                                                       
	sql = "DELETE FROM " + nomeTabella;                                                                                                                 
	sql += " WHERE " + chiave + " IS ?";                                                                                                                
	parametri = [valoreChiave];                                                                                                                         
	logga(sql);                                                                                                                                         
	eseguiSql(sql, parametri, function(callback){eliminazioneRigheCorretta(callback)}, function(sql, callback){eliminazioneRigheErrata(sql, callback)});
}                                                                                                                                                       
                                                                                                                                                        
function eliminazioneRigheCorretta(callback){callback(true, "delete")}                                                                                  
function eliminazioneRigheErrata(sql, callback){logga("Impossibile effettuare operazione: " + sql); callback(false, "delete")}                          
                                                                                                                                                        
/*                                                                                                                                                      
Elimina una tabella                                                                                                                                     
*/                                                                                                                                                      
                                                                                                                                                        
                                                                                                                                                        
function eliminaTabella(nomeTabella, callback) {                                                                                                        
	sql = "DROP TABLE IF EXISTS " + nomeTabella;                                                                                                        
	logga(sql);                                                                                                                                         
	eseguiSql(sql, [], function(){eliminazioneTabellaCorretta(callback)}, function(){eliminazioneTabellaErrata(sql, callback)});                        
}                                                                                                                                                       
                                                                                                                                                        
function eliminazioneTabellaCorretta(callback){callback(true, "drop")}                                                                                  
function eliminazioneTabellaErrata(sql, callback){logga("Impossibile effettuare operazione: " + sql); callback(false, "drop")}                          
                                                                                                                                                        
/*                                                                                                                                                      
Esegui una query sul database                                                                                                                           
*/                                                                                                                                                      
                                                                                                                                                        
function seleziona(sql, parametri, callback)                                                                                                            
{                                                                                                                                                       
	if((new RegExp("SELECT")).test(sql))                                                                                                                
	{                                                                                                                                                   
		eseguiSql(sql, parametri, function(tx, results){
									selezioneCorretta(results, callback);
								  },
								  function(tx, error){
									selezioneErrata(sql, error, callback);
								  }
		);    
	}                                                                                                                                                   
	else selezioneErrata(sql, null, "Impossibile eseguire selezione: il comando SELECT  mancante o scritto errato.", callback);                              
}                                                                                                                                                       
                                                                                                                                                        
function selezioneCorretta(results, callback)                                                                                                           
{                                                                                                                                                       
	numeroRighe = results.rows.length;                                                                                                                  
	if (numeroRighe > 0)                                                                                                                                
	{                                                                                                                                                   
		oggetti = new Array();                                                                                                                          
		for (i = 0; i < numeroRighe; i++) {                                                                                                             
			var oggetto = results.rows.item(i);                                                                                                             
			oggetti.push(oggetto);                                                                                                                      
		}                                                                                                                                               
		callback(oggetti);                                                                                                       
	}                                                                                                                                                   
	else callback(null);                                                                                                                      
}                                                                                                                                                       
	                                                                                                                                                    
