function inizializzaDatabase(callback)                                                                                                                  
{                                                                                                                                                       
	generaDb = function(tx)                                                                                                                             
	{             
		tx.executeSql("CREATE TABLE IF NOT EXISTS Lingue (id PRIMARY KEY,lingua)");
		tx.executeSql("CREATE TABLE IF NOT EXISTS Musei (id PRIMARY KEY,id_lingua,nome,indirizzo,info,apertura,chiusura,biglietto,disabili,raggiungere,m1_rossa,m2_verde,m3_gialla,mp3,ordinamento int,ordinamento_prezzo int,latitudine,longitudine,icona,data_modifica,promozione)");
		tx.executeSql("CREATE TABLE IF NOT EXISTS News (id PRIMARY KEY,id_lingua,titolo,testo,immagine,data_modifica,tipologia)");
		tx.executeSql("CREATE TABLE IF NOT EXISTS Opere (id PRIMARY KEY,id_lingua,nome,autore,dettagli,descrizione,immagine,mp3,id_museo,principale,ordinamento int,data_modifica)");	                                                                                                                
	}                                                                                                                                                   
	if (!db)                                                                                                                                            
	{                                                                                                                                                   
		db = apriDb(NOME_DB, VERSIONE_DB, DESCRIZIONE_DB, DIMENSIONE_DB);                                                                               
	}                                                                                                                                                   
	db.transaction(function(tx) {                                                                                                                       
						generaDb(tx);                                                                                                                                   
				   },
  				   function(tx, error) {
						//console.log("Error generating db");
				   },
				   callback
	);                                                                                                                                                 
}   

// DA ELIMINARE ------------------------------------------------------------------------
function droppa(callback)
{
	eliminaDb = function(tx)                                                                                                                             
	{             
		tx.executeSql("DROP TABLE Lingue");
		tx.executeSql("DROP TABLE Musei");
		tx.executeSql("DROP TABLE News");
		tx.executeSql("DROP TABLE Opere");
		tx.executeSql("CREATE TABLE IF NOT EXISTS Lingue (id PRIMARY KEY,lingua)");
		tx.executeSql("CREATE TABLE IF NOT EXISTS Musei (id PRIMARY KEY,id_lingua,nome,indirizzo,info,apertura,chiusura,biglietto,disabili,raggiungere,m1_rossa,m2_verde,m3_gialla, mp3,ordinamento int,ordinamento_prezzo int,latitudine,longitudine,icona,data_modifica,promozione)");
		tx.executeSql("CREATE TABLE IF NOT EXISTS News (id PRIMARY KEY,id_lingua,titolo,testo,immagine,data_modifica,tipologia)");
		tx.executeSql("CREATE TABLE IF NOT EXISTS Opere (id PRIMARY KEY,id_lingua,nome,autore,dettagli,descrizione,immagine,mp3,id_museo,principale,ordinamento int,data_modifica)");
		window.localStorage.clear();
	}                                                                                                                                                   
	if (!db)                                                                                                                                            
	{                                                                                                                                                   
		db = apriDb(NOME_DB, VERSIONE_DB, DESCRIZIONE_DB, DIMENSIONE_DB);                                                                               
	}                                                                                                                                                   
	db.transaction(function(tx) {                                                                                                                       
						eliminaDb(tx);                                                                                                                                   
				   },
  				   function(tx, error) {
						//console.log("Error generating db");
				   },
				   callback
	);
   
}                                                                                                                                                    
