// Ritorna le lingue dell'app
// PARAMETRI:	lat -> passo latitudine
//				long -> passo longitudine
//				ordine -> passo eventuale filtro. Valorizzato con "distanza" o "costo"
//				callback -> funzione di ritorno
function getMuseiFromDb(lat, lng, keyword, ordine, callback) {
	
	if(ordine == "distanza") {
		riceviListaMuseiOrdinataPerOrdinamento(id_lingua, keyword, function(results) {buildPrincipali(lat, lng, results, ordine, callback);});
	}
	else if(ordine == "costo") {
		riceviListaMuseiOrdinataPerPrezzo(id_lingua, function(results) {buildPrincipali(lat, lng, results, ordine, callback);});
	}
	else {
		riceviListaMuseiOrdinataPerOrdinamento(id_lingua, keyword, function(results) {buildPrincipali(lat, lng, results, ordine, callback);});
	}
}

function buildPrincipali(lat, lng, results, ordine, callback) {
	var principali = new Array();
		
	for(index in results) {
		var object = results[index];
		lt2 = object.latitudine;
		lg2 = object.longitudine;
		distanza = calcolaDistanza(lat, lng, lt2, lg2, 1);
		var museo = new MuseiRicevuti(object.musei_id,object.id_lingua,object.musei_nome,object.indirizzo,object.info,object.apertura,object.chiusura,object.biglietto,object.disabili,object.raggiungere,object.m1_rossa,object.m2_verde,object.m3_gialla, object.musei_mp3,object.musei_ordinamento,object.ordinamento_prezzo,object.latitudine,object.longitudine,distanza,object.icona,object.musei_data_modifica,object.promozione);
		var opera = new Opere(object.opere_id,object.id_lingua,object.opere_nome,object.autore,object.dettagli,object.descrizione,object.immagine,object.opere_mp3,object.musei_id,object.principale,object.opere_ordinamento,object.opere_data_modifica);
		principali.push(new Principale(museo, opera));
	}
	if(ordine == "distanza")
	{
	ordinato = false;
		while(ordinato === false){
			ordinato = true;
			for(i = 0; i < principali.length -1; i++){
				if(principali[i+1].museo.distanza < principali[i].museo.distanza)
				{
					x = principali[i];
					principali[i] = principali[i+1];
					principali[i+1] = x;
					ordinato = false;
				}
			}
		}
	}
	callback(principali);
}

function calcolaDistanza(lat, lng, lt2, lg2, n_decimali){
    if(lat == 0 && lng == 0) {
        return "? "
    }
    lt1 = parseFloat(lat);
	lg1 = parseFloat(lng);
	pow = Math.pow;
	sqrt = Math.sqrt;
	round = Math.round;
	distanza = sqrt(pow((111 * lt1 - 111 * lt2), 2) + pow((111 * lg1 - 111 * lg2), 2));
	distanza = round(distanza * pow(10, n_decimali))/pow(10, n_decimali); 	// approssimazione ad un solo numero decimale.
	return distanza;
}

// Ritorna le lingue dell'app
// PARAMETRI:	lat -> passo latidudine
//				long -> passo longitudine
//				id_museo -> passo l'id del museo attuale
//				callback -> funzione di ritorno
function getMuseoFromDb(lat, lng, id_museo, callback) {
	riceviMusei(id_museo, id_lingua, function(results) {buildMuseo(lat, lng, results, callback);});
}

function buildMuseo(lat, lng, object, callback) {
	var museo;
	if(object) {
		lt1 = parseFloat(lat);
		lg1 = parseFloat(lng);
		lt2 = object.latitudine;
		lg2 = object.longitudine;
		distanza = calcolaDistanza(lt1, lg1, lt2, lg2, 1);
		museo = new MuseiRicevuti(object.id,object.id_lingua,object.nome,object.indirizzo,object.info,object.apertura,object.chiusura,object.biglietto,object.disabili,object.raggiungere,object.m1_rossa,object.m2_verde,object.m3_gialla, object.mp3,object.ordinamento,object.ordinamento_prezzo,object.latitudine,object.longitudine,distanza,object.icona,object.data_modifica,object.promozione);
	}
	callback(museo);
}

