// accorcia delle frasi mettendo dei ... al posto delle parole in mezzo
function shorten(charscount, str) {
	var startString = "";
	var endString = "";
	var i = 0;
	var words = str.split(" ");
    if(words.length <=1) {
        return str;
    }
	startString = words[i++];
	endString =  "..." + words[words.length-1];
	while(i <= words.length -2 && startString.length + endString.length < charscount) {
		startString += " " + words[i++];
	}
	if(i == words.length -1) {
		endString = " " + words[words.length-1];
	}
	console.log(str + " -> " + startString + endString);
	return startString + endString;
}


