function synchronizeDb(callback) {
	//console.log("synchronizeDb function called");
	
	var networkState = navigator.onLine;
	if(networkState){
		//console.log("connection available " + networkState + " starting to synchronize");
		var date;
		riceviUltimaSincronizzazione(function(date) {
			if(date == '2012-01-01') {
				populateDb(json_data_init,function(risultato){
					console.log(risultato);
					console.log(date);
					onlineUpdate(date, callback);
				});
			}
			else
			{
				console.log(date);
				onlineUpdate(date, callback);
			}
			
		});
	}
	else {
		callback(false);	// connection not available
	}
}

function onlineUpdate(date, callback) {
	richiestaAjax("", "?data=" + date, "POST", function(data, status){
					//console.log(data)
					if(data.length==1) {// setto 1 perch la data c' sempre!!!
						//console.log("no updates available");
						callback(true) //DA ELIMINARE
					}
					else {
						//console.log('************** Numero di updates: ' + data.length + '****************');
						//console.log('chiamo dbInterface');
						populateDb(data,function(risultato){
							//console.log(risultato);
							callback(true);
						});
					}
				});
}

function populateDb(data, callback) {
	//dataUltimoAggiornamento = data[0].data_max;
	//console.log('Data ultimo aggiornamento = ' + dataUltimoAggiornamento);
	//console.log(data);
	data_max = data[0].data_max;
	
	
	function popolaTabella(table, callback){	
		nomeTabella = table.nome_tabella;
		if(typeof table.nome_tabella !== "undefined")
		{
			if(nomeTabella === "cdm_lingue") funzioneInserimento = inserisciLingue;
			else if(nomeTabella === "cdm_musei") funzioneInserimento = inserisciMusei;
			else if(nomeTabella === "cdm_opere") funzioneInserimento = inserisciOpere;
			else if(nomeTabella === "cdm_news") funzioneInserimento = inserisciNews;
		}
		
		function eseguiPopolazioneTabella(table, callback)
		{
			if(!table || !table.data || table.data.length === 0){
				populateDb(data, callback)
			}
			else
			{
				oggetto = table.data.pop();
				funzioneInserimento(oggetto, function(risultato){
					popolaTabella(table, callback);
				});
			}
		}
		eseguiPopolazioneTabella(table, callback);
	}
	
	if(!data || data.length === 1)
	{
		creaLog(data_max, callback);
	}
	else 
	{
		table = data.pop();
		popolaTabella(table, callback);
	}
}

var totaleLingue = 0;
var lingueInserite = 0;
var totaleMusei = 0;
var museiInseriti = 0;
var totaleOpere = 0;
var opereInserite = 0;
var totaleNews = 0;
var newsInserite = 0;

function populateDb_new(data, callback) {
	var data_max = data[0].data_max;
	
	console.log(data_max);
	
	if(!data || data.length === 1)
	{
		creaLog(data_max, callback);
	}
	else 
	{
		for(var tableIndex in data) {
			var table = data[tableIndex];
			if(typeof table.nome_tabella !== "undefined")
			{
				if(table.nome_tabella === "cdm_lingue") {
					totaleLingue = table.data.length;
					for(var rowIndex in table.data) {
						var oggetto = table.data[rowIndex];
						inserisciLingue(oggetto, function(result) { inserisciLingueResult(result, callback); });
					}
				}
				if(table.nome_tabella === "cdm_musei") {
					totaleMusei = table.data.length;
					for(var rowIndex in table.data) {
						var oggetto = table.data[rowIndex];
						inserisciMusei(oggetto, function(result) { inserisciMuseiResult(result, callback); });
					}
				}
				if(table.nome_tabella === "cdm_opere") {
					totaleOpere = table.data.length;
					for(var rowIndex in table.data) {
						var oggetto = table.data[rowIndex];
						inserisciOpere(oggetto, function(result) { inserisciOpereResult(result, callback); });
					}
				}
				if(table.nome_tabella === "cdm_news") {
					totaleNews = table.data.length;
					for(var rowIndex in table.data) {
						var oggetto = table.data[rowIndex];
						inserisciNews(oggetto, function(result) { inserisciNewsResult(result, callback); });
					}
				}
			}
		}
	}
}

function inserisciLingueResult(result, callback) {
	lingueInserite++;
	console.log(totaleLingue + " - " + lingueInserite);
	if(totaleLingue == lingueInserite && totaleMusei == museiInseriti && totaleOpere == opereInserite && totaleNews == newsInserite) {
		callback();
	}
}

function inserisciMuseiResult(result, callback) {
	museiInseriti++;
	console.log(totaleMusei + " - " + museiInseriti);
	if(totaleLingue == lingueInserite && totaleMusei == museiInseriti && totaleOpere == opereInserite && totaleNews == newsInserite) {
		callback();
	}
}

function inserisciOpereResult(result, callback) {
	opereInserite++;
	console.log(totaleOpere + " - " + opereInserite);
	if(totaleLingue == lingueInserite && totaleMusei == museiInseriti && totaleOpere == opereInserite && totaleNews == newsInserite) {
		callback();
	}
}

function inserisciNewsResult(result, callback) {
	newsInserite++;
	console.log(totaleNews + " - " + newsInserite);
	if(totaleLingue == lingueInserite && totaleMusei == museiInseriti && totaleOpere == opereInserite && totaleNews == newsInserite) {
		callback();
	}
}


