//creazione myscroll
function crea_scroll() {
	distruggi_scroll()
	myScroll = new iScroll('wrapper', {snap: 'li', momentum: false, hScrollbar: false, vScrollbar: false/*, onScrollEnd: function (){ mostra_foto()}*/})
}

//creazione myscroll mappa
function crea_scroll_mappa() {
	distruggi_scroll()
	myScroll_mappa = new iScroll('wrapper_mappa', {snap: 'li', momentum: true, hScrollbar: false, vScrollbar: false,hScroll:false/*, onScrollEnd: function (){ mostra_foto()}*/})
}

