function crea_scroll_dettaglio() {
	distruggi_scroll()
	myScroll_dettaglio = new iScroll('wrapper_dettaglio', {momentum: true, hScrollbar: false, vScrollbar: false,hScroll:false/*, onScrollEnd: function (){ mostra_foto()}*/})
}

