//creazione myscroll mappa statica
function crea_scroll_mappa_statica() {
	distruggi_scroll()
	myScroll_mappa_statica = new iScroll('wrapper_mappa_statica', {hScrollbar: false, vScrollbar: false,zoom: true/*, onScrollEnd: function (){ mostra_foto()}*/})
}

//qi cancelliamo sempre gli iscroll!!
function distruggi_scroll() {
	if(myScroll) {
		myScroll.destroy();
		myScroll = null;
	}
	if(myScroll_mappa) {
		myScroll_mappa.destroy();
		myScroll_mappa = null;
	}
	if(myScroll_dettaglio) {
		myScroll_dettaglio.destroy();
		myScroll_dettaglio = null;
	}
	if(myScroll_dettaglio_opere) {
		myScroll_dettaglio_opere.destroy();
		myScroll_dettaglio_opere = null;
	}
	if(myScroll_dettaglio_opere_foto) {
		myScroll_dettaglio_opere_foto.destroy();
		myScroll_dettaglio_opere_foto = null;
	}
	if(myScroll_credits) {
		myScroll_credits.destroy();
		myScroll_credits = null;
	}
	if(myScroll_mappa_statica) {
		myScroll_mappa_statica.destroy();
		myScroll_mappa_statica = null;
	}
}

