//cambiamo pagina
function carica_pagina(quale,id,rosso) {
	console.log("entro in carico pagina " + quale)
	if(map) {
		map=null;
	}
	//elimino gli scroll!
	distruggi_scroll()
	//nascondo le pagine
	$("#"+attuale_pagina).hide()
	//mostro quella che mi serve
	$("#pagina_"+quale).show()
	attuale_pagina="pagina_"+quale;//valorizzo per volta dopo...
	//inizializzo in base a scelta
	
	console.log("entro in carico pagina 222 " + quale)
	
	if(quale=="principale") {
		$('#pagina_mappa > *').remove();
		$('#pagina_dettaglio > *').remove();
		crea_html_principale()
		crea_livelli()
		crea_scroll()
		evidenzia_icona(1)
	}
	if(quale=="mappa") {
		$('#pagina_principale > *').remove();
		$('#pagina_dettaglio > *').remove();
		crea_html_mappa()
		evidenzia_icona(2)
		evidenzia_bottone(rosso,'no')
		if(rosso==2) {
			getMusei(mia_latitudine, mia_longitudine, '', 'costo', getMuseiResult)
		}
		if(rosso==3) {
			getMusei(mia_latitudine, mia_longitudine, '', 'distanza', getMuseiResult)
		}
	}
	if(quale=="share") {
		$('#pagina_mappa > *').remove();
		$('#pagina_principale > *').remove();
		$('#pagina_dettaglio > *').remove();
		evidenzia_icona(3)
		crea_html_credits()
	}
	if(quale=="dettaglio") {
		console.log("entro in carico pagina dettaglio " + quale)
		$('#pagina_principale > *').remove();
		$('#pagina_mappa > *').remove();
		crea_html_dettaglio()
		
		if(html_bottone_da_conservare==0) {
			html_bottone_da_conservare='<a href="javascript:;" onclick="carica_pagina(\''+vecchio_quale+'\',\''+vecchio_id+'\',\''+vecchio_rosso+'\')"><img src="img/indietro_'+id_lingua+'.png" width="90" height="30"></a>'
		}
		console.log("primo entro in getmuseo >"+id)
		getMuseo(mia_latitudine, mia_longitudine, id, getMuseoResult)
		
		
		
	}
	$("#dettaglio_indietro_btn").html('<a href="javascript:;" onclick="carica_pagina(\''+vecchio_quale+'\',\''+vecchio_id+'\',\''+vecchio_rosso+'\')"><img src="img/indietro_'+id_lingua+'.png" width="90" height="30"></a>');
	vecchio_quale=quale
	vecchio_id=id
	vecchio_rosso=rosso
	console.log("entro in carico pagina fine!! " + quale)
}

var my_media;

//fermo tts
function ferma_musica() {
	my_media.stop();
	my_media.release();
	my_media = null;
}

//faccio partire il text to speech
function musica_maestro(el, src) {
	console.log(src);
	console.log(el);
	if(my_media) {
		$('#tts > img').attr('src', 'img/tts.png');
		ferma_musica();
	} else {
		$('#tts > img').attr('src', 'img/tts_pausa.png');
		my_media = new Media('/android_asset/www/' + src);
	    my_media.play();
	}
	el.bind('remove', function(ev) { if (ev.target === this) { ferma_musica(); } });
}

