//flippa banner
function gira_banner() {
	numerazione_banner_social=numerazione_banner_social+1;
	if(navigator.userAgent.match(/Android/i)) {
		if(numerazione_banner_social % 2==0) {
			$(".front").fadeOut("fast")
			$(".back").fadeIn("fast")
		} else {
			$(".back").fadeOut("fast");
			$(".front").fadeIn("fast");
		}
	} else {
		if(numerazione_banner_social % 2==0) {
			$(".hover").addClass('flip')
		} else {
			$(".hover").removeClass('flip');
		}
	}
	setTimeout("gira_banner()",5000);
	/*$('.hover').hover(function(){
		$(this).addClass('flip');
	},function(){
		$(this).removeClass('flip');
	});*/
}

