function chiudi_mappa_statica() {
	if(myScroll_mappa_statica) {
		myScroll_mappa_statica.destroy();
		myScroll_mappa_statica = null;
	}
	$("#contengo_mappa_statica").remove()
	$("#bottoncino_indietro_mappa").remove()
	$("#wrapper_mappa_statica").remove()
	$("#scroller_mappa_statica").remove()
	
	if(myScroll) {
		myScroll.refresh();
	}
	if(myScroll_mappa) {
		myScroll_mappa.refresh();
	}
	if(myScroll_dettaglio) {
		myScroll_dettaglio.refresh();
	}
	if(myScroll_dettaglio_opere) {
		myScroll_dettaglio_opere.refresh();
	}
	if(myScroll_dettaglio_opere_foto) {
		myScroll_dettaglio_opere_foto.refresh();
	}
	if(myScroll_credits) {
		myScroll_credits.refresh();
	}
	if(myScroll_mappa_statica) {
		myScroll_mappa_statica.refresh();
	}
	
}

