//devideready
function onDeviceReady() {
	console.log('device ready!!!!!!!!!!!!!!!!!!');
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(function(result) {success(result); loadAll();}, function(){ error(); loadAll();});
    } else {
        error('not supported');
        loadAll();
    }
	installChildBrowser()
}

