function startup(callback) {
	inizializzaDatabase(function() {
							synchronizeDb(function()
							{
								callback();
							});
						}
	);
}

// Ritorna le lingue dell'app
// PARAMETRI:	callback -> funzione di ritorno
function getLingue(callback) {
	getLingueFromDb(callback);
}

// Setta la lingua di default
// PARAMETRI:	id_lingua -> passo l'id lingua selezionato dall'utente
//				callback -> funzione di ritorno
function setLinguaDefault(id_lingua) {
	setLinguaDefaultFromDb(id_lingua);
}

// Ritorna la lingua di default
// PARAMETRI:	callback -> funzione di ritorno
function getLinguaDefault(callback) {
	getLinguaDefaultFromDb(callback);
}

// Ritorna le lingue dell'app
// PARAMETRI:	tipo -> passo il tipo di news che mi serve
//				callback -> funzione di ritorno
function getNews(tipo, callback) {
	getNewsFromDb(tipo, callback);
}

// Ritorna le lingue dell'app
// PARAMETRI:	lat -> passo latidudine
//				long -> passo longitudine
//				ordine -> passo eventuale filtro. Valorizzato con "distanza" o "costo"
//				callback -> funzione di ritorno
function getMusei(lat, lng, keyword, ordine, callback) {
	getMuseiFromDb(lat, lng, keyword, ordine, callback);
}

// Ritorna le lingue dell'app
// PARAMETRI:	lat -> passo latidudine
//				long -> passo longitudine
//				id_museo -> passo l'id del museo attuale
//				callback -> funzione di ritorno
function getMuseo(lat, long, id_museo, callback) {
	getMuseoFromDb(lat, long, id_museo, callback);
}

// Ritorna le lingue dell'app
// PARAMETRI:	lat -> passo latidudine
