//mappa google per ricerca: ci devono essere tutte le icone dei musei + la nostra + popup se possibile che si apre e ti indica la strada
function carica_mappa_ricerca(oggettone) {
	var markers = [];
	var conteggio_icone=0
	
	if(map) {
		if (markers_luca) {
			for (i in markers_luca) {
				markers_luca[i].setMap(null);
			}
			markers_luca.length = 0;
		}
	} else {
		abilito_var_google()
		// Create the map 
		// No need to specify zoom and center as we fit the map further down.
		map = new google.maps.Map(document.getElementById("mappa_immagine"), {
		  mapTypeId: google.maps.MapTypeId.ROADMAP
		});
	
	}//endif iniziale
		
	
	// Define the list of markers.
	
	for(risultatiIndex in oggettone) {
		var risultato = oggettone[risultatiIndex];
		var completa_var=array_ricreato[conteggio_icone]
		musei = oggettone[risultatiIndex].museo;
		markers.push([musei.latitudine, musei.longitudine, completa_var, ''+musei.nome+'',''+musei.indirizzo+''])
		conteggio_icone=conteggio_icone+1
	}
	
	markers.push([mia_latitudine, mia_longitudine, image_io, '',''+mia_latitudine+','+mia_longitudine+''])
	// Create the markers
	for (index in markers) {
		addMarker(markers[index]);
	}
	function addMarker(data) {
		var marker = new google.maps.Marker({
			position: new google.maps.LatLng(data[0], data[1]),
			map: map,
			title: data[3],
			icon: data[2],
		});
		
		var contenuto = "<div class=\"gmap_testo\">"+data[3]+"<br><span class='gmap_indirizzo'>"+data[4]+"</span><div class=\"gmap_indicazioni\">Indicazioni stradali<br><a href=\"http://www.google.com/maps?saddr="+data[3]+" "+data[4].replace(/<\/br>/gi," ")+"\" target=\"_blank\">Da qui</a> <a href=\"http://www.google.com/maps?daddr="+data[3]+" "+data[4].replace(/<\/br>/gi," ")+"\" target=\"_blank\">Fino a qui</a></div><div style='clear:both'></div></div>";
		google.maps.event.addListener(marker, "click", function() {
			//openInfoWindow(marker);
			title.innerHTML = contenuto;
			pin.set("position", marker.getPosition());
			infowindow.open(map, marker);
		});
		markers_luca.push(marker);
	}

	// Zoom and center the map to fit the markers
	// This logic could be conbined with the marker creation.
	// Just keeping it separate for code clarity.
	var bounds = new google.maps.LatLngBounds();
	for (index in markers) {
		var data = markers[index];
		bounds.extend(new google.maps.LatLng(data[0], data[1]));
	}
	map.fitBounds(bounds);
	
	// Handle the DOM ready event to create the StreetView panorama
	// as it can only be created once the DIV inside the infowindow is loaded in the DOM.
	var panorama = null;
	var pin = new google.maps.MVCObject();
	google.maps.event.addListenerOnce(infowindow, "domready", function() {
		panorama = new google.maps.StreetViewPanorama(streetview, {
			navigationControl: false,
			enableCloseButton: false,
			addressControl: false,
			linksControl: false,
			visible: true
		});
		panorama.bindTo("position", pin);
	});
	
	// Set the infowindow content and display it on marker click.
	// Use a 'pin' MVCObject as the order of the domready and marker click events is not garanteed.
	function openInfoWindow(marker) {
		title.innerHTML = contenuto;
		pin.set("position", marker.getPosition());
		infowindow.open(map, marker);
	}
	if(smartphone=="si") {
		$("#mappa_immagine").css({'display':'block' });
	}
}

