function onSuccessScheda(data){
		var LAN = document.getElementById('lansel').value;
		var text = '';
		var code = '', prodotto = '', st_descrizione = '', customer_descrizione = '';
		var normativa = '';
		var gruppi;
		
		if(data.items) {
			text += '<table border="0" width="100%" style="font-family:Verdana, Geneva, sans-serif; font-size:11px;">';
			J.each(data.items, function() {
				code = this.code;
				prodotto = this.prodotto;
				st_descrizione = this.st_descrizione;
				customer_descrizione = this.customer_descrizione;
				normativa = this.normativa;
				gruppi = this.gruppi;
				text += '<tr>'+
            	'<td>'+
           	    '<img src="images/Contital_i2r-logo.png">'+
               	'</td>'+
                '<td>'+
                	'<table border="0" width="100%" cellpadding="3">'+
                   	'<tr><td><b>Prod:</b> '+prodotto+'</td></tr>'+
                    '<tr><td><b>Cod:</b> '+code+'</td></tr>'+
                    '<tr><td><b>Std:</b> '+st_descrizione+'</td></tr>'+
                    '<tr><td><b>Customer:</b> '+customer_descrizione+'</td></tr>'+
                    '</table>'+
                '</td>'+
            	'</tr><tr><td><br></td></tr>';
				if(gruppi) {
					text += '<tr><td colspan = "2"><table border="0" width="100%" cellpadding="3">';
					var idgruppo = '', desgruppo = '';
					var attributi;
					J.each(gruppi, function() { 
						idgruppo = this.idgruppo;
						desgruppo = this.desgruppo;
						attributi = this.attributi;
						text += '<tr bgcolor="#666666">' +
								'<td align = "center" colspan="2" style="color:#FFF">'+desgruppo+'</td>' +
								'</tr>';
						if(attributi){
							var label = '', valore = '';
							J.each(attributi, function() { 
								label = this.label;
								valore = this.valore;
								if(valore != ""){
									text += '<tr>' +
									'<td style="border-bottom:1px solid #999">'+label+'</td>' +
									'<td style="border-bottom:1px solid #999">: '+valore+'</td>' +
									'</tr>';
								}
							});
						}
					});
					text += '</table></td></tr>';
				}
			});
			text += '</table>';
			document.getElementById('testoscheda').innerHTML = text;
		} else {
			//form di richiesta scheda tecnica
			var  labform = 'Richiesta scheda tecnica';
			if(LAN == 'it' || LAN == 'IT'){
				labform = 'Richiesta scheda tecnica';
			}
			else if(LAN == 'en' || LAN == 'EN'){
				labform='Datasheet request';
			}
			else if(LAN == 'fr' || LAN == 'FR'){
				labform='Demande de fiche technique';
			}
			else if(LAN == 'de' || LAN == 'DE'){
				labform='Anfrage nach datenblatt';
			}
			document.getElementById('titlePopupScheda').innerHTML = labform;
			document.getElementById('testoscheda').innerHTML = '';
			document.getElementById('richiestascheda').style.display = 'inline';
		}
	}


