function getMappaGoogle(indice){
	var indirizzo = '';
	
	if(indice == 1){
		indirizzo = 'KM. 192 VIA APPIA, 81052 Pignataro Maggiore, CE';
	}else if(indice == 2){
		indirizzo = 'Innovation House, Hortonwood 30, Telford, Shropshire TF1 7AB,United Kingdom';
	}
	
	
	
	if(indirizzo != ""){
		
		var latlng = new google.maps.LatLng(41.87194,12.56738);

		J('#map_canvas').gmap({ 'center': latlng, 'zoom': 12 });
		J('#map_canvas').gmap('search', { 'address': indirizzo }, function(results) {    
		 
		// Get the map    
		var map = J('#map_canvas').gmap('get','map');  
		  
		// Pan to first result    
		map.panTo(results[0].geometry.location);
		
		J('#map_canvas').gmap('addMarker', { 'position': results[0].geometry.location });
		//Q('#to').val(results[0].geometry.location);
		});
		J('#mapnotfound').html('');
		
	}else{
		var latlng = new google.maps.LatLng(41.87194,12.56738);

		J('#map_canvas').gmap({ 'center': latlng, 'zoom': 12 });
		J('#mapnotfound').html('Indirizzo non trovato');
	}

}




