	function getElencoForme(LAN, MATERIALE){
		if (!db) {
    	    var db = window.openDatabase("DB", "1.0", "i2r", 2000000);
    	    if (db === null) {
                databaseOutput("Database could not be opened.");    
                return;
    	    }
		}
		
		var labelForma = "Forma";
		if(LAN == "EN"){
			labelForma = "Shape";
		}else if(LAN == "FR"){
			labelForma = "Forme";
		}else if(LAN == "DE"){
			labelForma = "Form";
		}
		
		var forma = document.getElementById('forma_sel').value;
		
		var text="";	
		db.transaction(function (tx) {
			
				if(MATERIALE == ""){
					
					tx.executeSql('SELECT DISTINCT FORMA, DESCRIZIONE FROM FORME WHERE LANG = ? ORDER BY DESCRIZIONE', [LAN], function (tx, results) {
						var len = results.rows.length;
						text += '<option>- '+labelForma+' -</option>';
						text += '<option value = "">Tutti</option>';
						for (var i = 0; i < len; i++) {
							text += '<option value = "'+results.rows.item(i).Forma+'">'+results.rows.item(i).Descrizione+'</option>';
						}
						
						//visualizziamo output nel select
						databaseOutputForma(text);
						if(forma == ""){
							Q("#forma").selectmenu('refresh', true);
						}
					}); // chiudo funzione in executesql
				}else{
					
					tx.executeSql('SELECT DISTINCT FORMA, DESCRIZIONE FROM FORME WHERE LANG = ? AND MATERIALE = ? ORDER BY DESCRIZIONE', [LAN, MATERIALE], function (tx, results) {
						var len = results.rows.length;
						text += '<option>- Forma -</option>';
						text += '<option value = "">Tutti</option>';
						for (var i = 0; i < len; i++) {
							text += '<option value = "'+results.rows.item(i).Forma+'">'+results.rows.item(i).Descrizione+'</option>';
						}
						
						//visualizziamo output nel select
						databaseOutputForma(text);
						if(forma == ""){
							Q("#forma").selectmenu('refresh', true);
						}
						//databaseOutputForma(text);
					}); // chiudo funzione in executesql
				}
			
				
	
		});	 // fine db transazione
		
		
	}
	
	
