	function getElencoMateriale(LAN){
		if (!db) {
    	    var db = window.openDatabase("DB", "1.0", "i2r", 2000000);
    	    if (db === null) {
                databaseOutput("Database could not be opened.");    
                return;
    	    }
		}
		var labelALL = "Tutti";
		var labelMateriale = "Materiale";
		if(LAN == "EN"){
			labelMateriale = "Material";
			labelALL = "All";
		}else if(LAN == "FR"){
			labelMateriale = "Matériel";
			labelALL = "All";
		}else if(LAN == "DE"){
			labelMateriale = "Material";
			labelALL = "All";
		}
		
		var materiale = document.getElementById('materiale_sel').value;
		var forma = document.getElementById('forma_sel').value;
		
		var text="";	
		db.transaction(function (tx) {
			
			if(forma == ""){
				tx.executeSql('SELECT CODICE, DESCRIZIONE FROM MATERIALI WHERE LANG = ? ORDER BY DESCRIZIONE', [LAN], function (tx, results) {
					var len = results.rows.length;
					text += '<option value = "">- '+labelMateriale+' -</option>';
					text += '<option value = "">'+labelALL+'</option>';
					for (var i = 0; i < len; i++) {
						text += '<option value = "'+results.rows.item(i).Codice+'">'+results.rows.item(i).Descrizione+'</option>';
					}
					
					//visualizziamo output nel select
					databaseOutputMateriale(text);
					if(materiale == ""){
						Q("#materiale").selectmenu('refresh', true);
					}
				}); // chiudo funzione in executesql
			}else{
				tx.executeSql('SELECT FORME.MATERIALE, MATERIALI.DESCRIZIONE FROM FORME LEFT JOIN MATERIALI ON FORME.MATERIALE = MATERIALI.CODICE AND FORME.LANG = MATERIALI.LANG WHERE FORME.LANG = ? AND FORME.FORMA LIKE ? ORDER BY MATERIALI.DESCRIZIONE', [LAN, forma], function (tx, results) {
					var len = results.rows.length;
					text += '<option value = "">- '+labelMateriale+' -</option>';
					text += '<option value = "">Tutti</option>';
					for (var i = 0; i < len; i++) {
						text += '<option value = "'+results.rows.item(i).Codice+'">'+results.rows.item(i).Descrizione+'</option>';
					}
					
					//visualizziamo output nel select
					databaseOutputMateriale(text);
					if(materiale == ""){
						Q("#materiale").selectmenu('refresh', true);
					}
				}); // chiudo funzione in executesql
			}
				
	
		});	 // fine db transazione
		
		
	}
	
