function searchAddMessage(p_message){
    _INBOX_MESSAGES['mid_' + p_message.id] = {
        id: p_message.id,
        subject: p_message.subject,
        mabstract: p_message.mabstract,
        date: p_message.date,
        body: p_message.body,
        status: p_message.status
    };
    
    inboxRenderHeader(p_message.id);
	
	//inboxUpdateUnreadMessage();
}
