function parseDateWithDbFormat(string) {
	var reggie = /(\d{4})-(\d{2})-(\d{2}) (\d{2}):(\d{2}):(\d{2})/g;
	var tokens = reggie.exec(string);
	if (tokens.length != 7)
		return new Date();
	else
		return new Date(tokens[1], tokens[2] - 1, // mesi partono da 0 in js
		tokens[3], tokens[4], tokens[5], tokens[6]);
};

