			//var l_query = 'SELECT nome, COUNT(*) AS num_articles FROM '+_PREFERITI_TAG_DB_NAME+' GROUP BY nome ORDER BY num_articles';
			var l_query = 'SELECT nome FROM ' + _PREFERITI_TAG_DB_NAME + ' GROUP BY nome ORDER BY nome';
