                //alert('OK eliminazione preferito.');
                tx.executeSql('DELETE FROM ' + _PREFERITI_TAG_DB_NAME + ' WHERE artid=?;', [_CURRENT_ARTICLE.artid], querySuccessTag, queryErrorTag);
