	else
		console.log('setFlipperCurrentState ' + p_testata + ' ' + p_issue + ' ' + p_edizione + ' ' + p_pagina);
	_CURRENT_FLIPPER_TESTATA = p_testata;
	_CURRENT_FLIPPER_ISSUE = p_issue;
