      function calculateDistances(position) {
	  var latitude=position.coords.latitude;
	var longitude=position.coords.longitude;
	//alert('Lat:' + latitude);
	//alert('Lng:' + longitude);
	  var origin = new google.maps.LatLng(latitude, longitude);
	  geocoder = new google.maps.Geocoder();
        var service = new google.maps.DistanceMatrixService();
        service.getDistanceMatrix(
          {
            origins: [origin],
            destinations: [destination],
            travelMode: google.maps.TravelMode.DRIVING,
            unitSystem: google.maps.UnitSystem.IMPERIAL,
            avoidHighways: false,
            avoidTolls: false
          }, callback);
		  
      }

      function callback(response, status) {
	  //alert('1');
        if (status != google.maps.DistanceMatrixStatus.OK) {
          //alert('Error was: ' + status);
        } else {
		//alert('2');
          var origins = response.originAddresses;
          var destinations = response.destinationAddresses;
          var outputDiv = document.getElementById('outputDiv');
          outputDiv.innerHTML = '';
//alert('3');
          for (var i = 0; i < origins.length; i++) {
		  //alert('4');
            var results = response.rows[i].elements;
            for (var j = 0; j < results.length; j++) {
			//alert('5');
			distance = results[j].distance.value;
								 distance = distance / 1609.344;
								 //alert('Dist:' + distance);
								 gotResult=1;
					if (distance<distanceLimit){
					//alert('success');
				  outputDiv.innerHTML += '<p><strong>Rocky Port Grill and Fish Market:</strong></p><p><strong>&nbsp;</strong>&nbsp;</p><ul><li>Text keyword WIN to 619-377-7397 for a chance to Win a FREE Entr&eacute;e!</li></ul><p>&nbsp;</p><ul><li>Text your Name &amp; Email to 619-754-9732 to get a FREE Seafood Buying Guide!</li></ul><p>&nbsp;</p><p>*Text and data usage rates may apply.</p>';
				  }
				  else{
				  //alert('fail');
				  outputDiv.innerHTML += '<p>Sorry, not in range to receive special offer.</p>';
				  }
            }
          }
        }
      }


